SELECT 'OLDEST' as TYPE, NAME, MIN(WORKER.BIRTHDAY) as BIRTHDAY from WORKER where BIRTHDAY = (SELECT MIN(WORKER.BIRTHDAY) as BIRTHDAY from WORKER) group by NAME UNION SELECT 'YOUNGEST' as TYPE, NAME, MAX(WORKER.BIRTHDAY) as BIRTHDAY from WORKER where BIRTHDAY = (SELECT MAX(WORKER.BIRTHDAY) as BIRTHDAY from WORKER)
group by NAME;